# Inject the CSS into the Streamlit app
if PLANT_BACKGROUND_BASE64: # Only apply if Base64 string is provided
    st.markdown(background_css, unsafe_allow_html=True)
else:
    st.warning("Please run the Base64 conversion script and paste the output into 'PLANT_BACKGROUND_BASE64' in app.py for the background image to appear.")


# Load the trained models
@st.cache_resource
def load_model(model_name):
    """Loads a pre-trained Keras model."""
    try:
        if model_name == "ResNet":
            model = tf.keras.models.load_model('esca_resnet_model.h5')
        elif model_name == "MobileNet":
            model = tf.keras.models.load_model('esca_mobilenet_model.h5')
        return model
    except Exception as e:
        st.error(f"Error loading model {model_name}: {e}. Make sure the .h5 file is in the same directory.")
        return None

st.title("Esca Disease Detection for Plants  🍂🌿")

st.write("Welcome! This tool helps identify whether a plant leaf image shows signs of Esca disease or is healthy.")

# Model selection
selected_model = st.selectbox("1. Choose a classification model:", ("ResNet", "MobileNet"))

# File uploader
uploaded_file = st.file_uploader("2. Upload an image of a plant leaf...", type=["jpg", "jpeg", "png"])

if uploaded_file is not None:
    # Display the uploaded image
    img = Image.open(uploaded_file)
    st.image(img, caption="Uploaded Leaf Image", use_column_width=True)
    st.write("")
    st.write("Analyzing the leaf image...")

    # Preprocess the image
    img_array = np.array(img)

    # Convert to RGB if not already (some images might be RGBA or grayscale)
    if img.mode != 'RGB':
        img = img.convert('RGB')
        img_array = np.array(img)

    # Resize image to model's expected (height=80, width=45)
    # PIL.Image.resize expects (width, height)
    img_resized = Image.fromarray(img_array).resize((45, 80))
    img_resized_array = np.array(img_resized)

    # Expand dimensions to create a batch of 1 (required by Keras models)
    img_batch = np.expand_dims(img_resized_array, axis=0)

    # Apply model-specific preprocessing
    processed_img = None
    if selected_model == "ResNet":
        processed_img = resnet_preprocess_input(img_batch)
    elif selected_model == "MobileNet":
        processed_img = img_batch / 255.0 # Rescale for MobileNet

    # Make prediction
    if processed_img is not None:
        model = load_model(selected_model)
        if model: # Ensure model loaded successfully
            prediction = model.predict(processed_img)

            # Get the class label (0 for esca, 1 for healthy)
            st.subheader("Classification Result:")
            if prediction[0][0] > 0.5:
                st.success(f"🌿 **Prediction: Healthy Leaf** (Confidence: {prediction[0][0]*100:.2f}%)")
                st.info("This leaf appears healthy based on the chosen model.")
            else:
                st.error(f"🍂 **Prediction: Esca Diseased Leaf** (Confidence: {(1 - prediction[0][0])*100:.2f}%)")
                st.info("This leaf shows signs of Esca disease based on the chosen model.")
        else:
            st.warning("Model could not be loaded. Please check the model file name and location.")
    else:
        st.warning("Could not process image for prediction. Please try again.")

st.sidebar.header("How to Use This App:")
st.sidebar.markdown(
    """
    * **Step 1: Choose a Model**
        Select either "ResNet" or "MobileNet" from the dropdown menu. These are the two image classification models available.

    * **Step 2: Upload a Plant Leaf Photo**
        Click the "Upload an image of a plant leaf..." button and select an image file (JPG, JPEG, or PNG) from your computer.

    * **Step 3: View the Result**
        Once uploaded, the app will display your image and then show a classification result:
        * **"Healthy Leaf"**: Indicates the model believes the leaf is healthy.
        * **"EsCa Diseased Leaf"**: Indicates the model believes the leaf shows symptoms of EsCa disease.
        A confidence percentage will also be displayed.

    ---
    *Disclaimer: This tool provides a classification based on trained models and is for demonstration purposes. For accurate diagnosis, consult with an agricultural expert.*
    """
)